      <header className = "bg-dark text-white display-5 p-5 hero-container">
         <h1 className = "hero-text"> {text} </h1>
         <div className = "hero-backdrop" style = {{backgroundImage: `url(${backdropUrl})`}}></div>
      </header>
    )
  }

